INSERT INTO `gpm_windowtemplate` VALUES ('f395a4bcba074984adc336606eaca7b4', '关于我们', 2, 'Html', '', 'Home', '', '', 1, 500, 400, '<ul> <li>ccbpm是一个100%的开源软件,包含工作流程引擎、表单引擎、组织结构管理、菜单管理等敏捷开发的基础模块。</li> <li>该开源软件由高凌公司从2003年开始研发到至今，经过多个版本迭代，并历经数千个项目于用户需求场景完成。</li> <li>设计严谨、考究抽象程度高、覆盖大部分客户应用需求，属于一款不可多得的应用国产的敏捷开发工具。</li> <li>源代码都发布在giee上，采用GPL开源协议进行开源，遵守GPL开源协议使用ccbpm合法有效。</li> <li>高凌公司对外提供现场培训、技术支持、协助集成、协助项目落地服务，对小微企业，小企业，中等企业，大企业收费8,12,18,23三个等级的付费。</li></ul>', 7, 1, 0, '', '', '', '', '', '', 0, 0, 0, 0, 0, NULL, NULL, NULL, NULL, NULL, NULL);
